const { fromEvent, interval, from } = rxjs;
const { filter, map, tap, mergeMap } = rxjs.operators;


const kattintas$ = fromEvent(document.getElementById('szin-form'), 'submit')
  .pipe(
    tap((esemeny) => {
      esemeny.preventDefault();
    }),
    map((esemeny) => {
      const ujSzin = esemeny.target.elements.szin.value;
      esemeny.target.reset();
      return ujSzin;
    }),
    tap((szin) => {
      console.log('%c Új szín kiválasztva', 'color: ' + szin);
    })
  );

const szamlalo$ = interval(1000).pipe(filter((num) => num % 2 === 0));

const csoRendszer$ = kattintas$.pipe(
  mergeMap((szin) => {
    return szamlalo$.pipe(
      tap((szam) => {
        console.log('%c ' + szam, 'color: ' + szin);
      })
    );
  })
);

